import React, { useState } from "react";
import "./sign-up.css";
import { FaUser, FaLock } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

function includeGmu(input) {
  const gmuRegex = /@gmu\.edu$/;
  return gmuRegex.test(input);
}

const SignUpForm = (props) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleInputChange = (setter) => (event) => {
    setter(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!includeGmu(email)) {
      setError("Please enter a GMU email");
      return;
    }

    const response = await fetch('/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password, name, email, phone }),
    });

    const data = await response.json();
    if (response.ok) {
      navigate('/');
    } else {
      setError(data.message);
    }
  };

  return (
    <>
      <h1 className="signin-title">RENTGMU</h1>
      <div className="wrapper">
        <form onSubmit={handleSubmit}>
          <h1>Sign up</h1>
          <div className="input-box">
            <input type="text" placeholder="First Name" required value={name} onChange={handleInputChange(setName)} />
          </div>
          <div className="input-box">
            <input type="text" placeholder="Email" required value={email} onChange={handleInputChange(setEmail)} />
          </div>
          <div className="input-box">
            <input type="text" placeholder="Username" required value={username} onChange={handleInputChange(setUsername)} />
            <FaUser className="icon" />
          </div>
          <div className="input-box">
            <input type="password" placeholder="Password" required value={password} onChange={handleInputChange(setPassword)} />
            <FaLock className="icon" />
          </div>
          <div className="input-box">
            <input type="text" placeholder="Phone" required value={phone} onChange={handleInputChange(setPhone)} />
          </div>

          {error && (
            <center>
              <p style={{ margin: "-15px 0 20px", color: "#ffa", position: "center" }}>
                {error}
              </p>
            </center>
          )}

          <button type="submit">Register</button>

          <div className="register-link">
            <p>return to <a href="#" onClick={() => window.location.reload()}>login</a></p>
          </div>
        </form>
      </div>
    </>
  );
};

export default SignUpForm;
