import React, { useState } from "react";
import "./sign-in.css";
import { FaUser, FaLock } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

const SignInForm = (props) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  document.body.style.background = "seagreen";
  document.body.style.minHeight = "100vh";

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    fetch(`http://localhost:3001/user/${currentUser}`)
    .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
    })
    .then(data => {
        if (data && data.success) {
            // Assuming the API response includes a 'data' field with user details
            localStorage.setItem("userInfo", JSON.stringify(data.data));
            console.log("User details updated successfully.");
        } else {
            console.log("No data received, check API response structure or endpoint.");
        }
    })
    .catch(error => {
        console.error("Failed to fetch user details:", error);
    });
    // API request to verify the username and password
    fetch('http://localhost:3001/verify-password', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ username, password })
    })
    .then(response => response.json())
    .then(data => {
      if (data.success) {
        props.setIsUserSignedIn(true);
        window.localStorage.setItem("userInfo", JSON.stringify({username, name: "User"})); // Assuming 'name' is needed
        window.localStorage.setItem("isLoggedIn", true);
        navigate('/home'); // Adjust as needed, depending on your route setup
      } else {
        setError(data.message);
      }
    })
    .catch(() => {
      setError("Failed to connect to the server. Please try again later.");
    });

    // Clear inputs only on success or don't clear
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    window.localStorage.setItem("isLoggedIn", true);
    props.setCurrentPage("/signup");
    navigate("/signup");
  };

  return (
    <>
      <h1 className="signin-title">RENTGMU</h1>
      <div className="wrapper">
        <form action="">
          <h1>Login</h1>
          <div className="input-box">
            <input
              type="text"
              placeholder="Username"
              required
              value={username}
              onChange={handleUsernameChange}
            />
            <FaUser className="icon" />
          </div>
          <div className="input-box">
            <input
              type="password"
              placeholder="Password"
              required
              value={password}
              onChange={handlePasswordChange}
            />
            <FaLock className="icon" />
          </div>

          {error && (
            <center>
              <p
                style={{
                  margin: "-15px 0 20px",
                  color: "#ffa",
                  position: "center",
                  fontSize: "12px",
                }}
              >
                {error}
              </p>
            </center>
          )}
          <div className="remember-forgot">
            {/* <label>
              <input type="checkbox" />
              Remember me
            </label> */}
            {/* <a href="#f">Forgot Password?</a> */}
          </div>

          <button type="submit" onClick={handleSubmit}>
            Login
          </button>

          <div className="register-link">
            <p>
              Don't have an account?{" "}
              <a href="#signup" onClick={handleSignUp}>
                Signup
              </a>
            </p>
          </div>
        </form>
      </div>
    </>
  );
};


export default SignInForm;
