import React from "react";
import "./sign-up.css";
// import "./index.css";
import { FaUser, FaLock } from "react-icons/fa";
import { useState } from "react";

import { useNavigate } from "react-router-dom";

function includeGmu(input) {
  const gmuRegex = /@gmu\.edu$/;
  return gmuRegex.test(input);
}

const SignUpForm = (props) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  const [error, setError] = useState("");
  window.localStorage.removeItem("isLoggedIn");
  const navigate = useNavigate();

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!includeGmu(email)) {
      setError("Please enter a GMU email");
      return;
    }

    // API call to your server's signup endpoint
    const response = await fetch('/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        name: name,
        email: email,
        username: username,
        password: password
      })
    });

    const data = await response.json();
    if (response.ok) {
      navigate('/');  // Adjust according to your routing setup, e.g., navigate to home or login
    } else {
      setError(data.message || "An error occurred during registration.");
    }
  };

  return (
    <>
      <h1 className="signin-title">RENTGMU</h1>
      <div className="wrapper">
        <form action="">
          <h1>Sign up</h1>
          <div className="input-box">
            <input
              type="text"
              placeholder="First Name"
              required
              value={name}
              onChange={handleNameChange}
            />
          </div>
          <div className="input-box">
            <input
              type="text"
              placeholder="Email"
              required
              value={email}
              onChange={handleEmailChange}
            />
          </div>
          <div className="input-box">
            <input
              type="text"
              placeholder="Username"
              required
              value={username}
              onChange={handleUsernameChange}
            />
            <FaUser className="icon" />
          </div>
          <div className="input-box">
            <input
              type="password"
              placeholder="Password"
              required
              value={password}
              onChange={handlePasswordChange}
            />
            <FaLock className="icon" />
          </div>

          {error && (
            <center>
              <p
                style={{
                  margin: "-15px 0 20px",
                  color: "#ffa",
                  position: "center",
                }}
              >
                {error}
              </p>
            </center>
          )}

          <button type="submit" onClick={handleSubmit}>
            Register
          </button>

          <div className="register-link">
            <p>
              return to{" "}
              <a href="#" onClick={reloadPage}>
                login
              </a>
            </p>
          </div>
        </form>
      </div>
    </>
  );
};

export default SignUpForm;
